    const options: AxiosRequestConfig = {
      baseURL: this.publicApiUrl,
      httpsAgent: this.agent,
      headers: {
        'Content-Type': 'application/json',
      },
    };
    // If provided, add our api key to all request headers.
    if (this.apiKey) {
      options.headers['x-api-key'] = this.apiKey;
    }
    return options;
