  private get<T>(
    path: string,
    decode: (x: any) => E.Either<Errors, T>,
    version?: string,
  ): TE.TaskEither<Error, T> {
    const options = this.buildOptions();

    if (version && options.baseURL) {
      options.baseURL = this.replaceApiVersion(options.baseURL, version);
    }

    return pipe(
      taskEitherWithError(() => axios.get(`${path}`, options)),
      TE.chain(r =>
        pipe(decode(r.data), E.mapLeft(errorsToError), TE.fromEither),
      ),
    );
  }

  private post<T>(
    path: string,
    data: any,
    decode: (x: any) => E.Either<Errors, T>,
    version?: string,
    headers?: Record<string, string>,
  ): TE.TaskEither<Error, T> {
    const options = this.buildOptions();

    if (version && options.baseURL) {
      options.baseURL = this.replaceApiVersion(options.baseURL, version);
    }

    // Add headers if any
    if (headers) {
      options.headers = { ...options.headers, ...headers };
    }

    return pipe(
      taskEitherWithError(() => axios.post(`${path}`, data, options)),
      TE.chain(r =>
        pipe(decode(r.data), E.mapLeft(errorsToError), TE.fromEither),
      ),
    );
  }

  private delete<T>(
    path: string,
    data: any,
    decode: (x: any) => E.Either<Errors, T>,
    headers?: Record<string, string>,
    version?: string,
  ): TE.TaskEither<Error, T> {
    return pipe(
      taskEitherWithError(() => {
        const options = this.buildOptions();

        if (version && options.baseURL) {
          options.baseURL = this.replaceApiVersion(options.baseURL, version);
        }

        if (headers) {
          options.headers = { ...options.headers, ...headers };
        }

        return axios.delete(`${path}`, {
          ...options,
          data,
        });
      }),
      TE.chain(r =>
        pipe(decode(r.data), E.mapLeft(errorsToError), TE.fromEither),
      ),
    );
  }

  public registerImxF(
    params: ImmutableMethodParams.ImmutableRegisterParams,
  ): TE.TaskEither<Error, ImmutableMethodResults.ImmutableRegisterResult> {
    const { etherKey, starkPublicKey, email } = params;
    let verifyEth = false;
    const currentTime = Math.round(new Date().getTime() / 1000);

    if (currentTime > 1633546800) {
      // 1633546800 = Thursday, 7 October 2021 06:00:00 GMT+11:00
      verifyEth = true;
    }

    if (!verifyEth) {
      return pipe(
        {
          body: {
            email,
            ether_key: etherKey.toLowerCase(),
            stark_key: starkPublicKey,
            nonce: 0,
          },
        },
        TE.of,
