  /**
   * Create a signable transfer
   *
   * @experimental - This function is only available for alpha testing
   */
  private async getSignableTransferV2(
    params: ImmutableMethodParams.ImmutableGetSignableTransferV2ParamsTS,
  ): Promise<ImmutableMethodResults.ImmutableGetSignableTransferV2Result> {
    return this.decodeForFunction(
      params,
      ImmutableMethodParams.ImmutableGetSignableTransferV2ParamsCodec.decode,
      this.getSignableTransferV2F,
    );
  }

  private convertToGetSignableTransferV2Params(
    params: ImmutableMethodParams.ImmutableTransferV2Params,
  ): ImmutableMethodParams.ImmutableGetSignableTransferV2Params {
    const signableTransferDetails = params.transfer_request.map(
      ({ token, amount, receiver }) => ({
        amount: token.type === ERC721TokenType.ERC721 ? '1' : amount.toString(),
        receiver: receiver.toLowerCase() as EthAddress,
        token: {
          type: token.type,
          data: this.getTokenBody(token),
        },
      }),
    );
    return {
      sender_ether_key: params.sender_ether_key.toLowerCase() as EthAddress,
      signable_requests: signableTransferDetails,
    };
  }

  /**
   * Create a transfer
   *
   * @experimental - This function is only available for alpha testing
   */
  public transferV2F(
    params: ImmutableMethodParams.ImmutableTransferV2Params,
  ): TE.TaskEither<Error, ImmutableMethodResults.ImmutableTransferV2Result> {
    return pipe(
      TE.bindTo('getSignableTransferV2Result')(
        this.getSignableTransferV2F(
          this.convertToGetSignableTransferV2Params(params),
        ),
      ),
      TE.bind('ethSignature', ({ getSignableTransferV2Result }) => {
        return this.signEthMessage(
          getSignableTransferV2Result.signable_message,
        );
      }),
      TE.bind(
        'transferRequestWithSignatures',
        ({ getSignableTransferV2Result }) => {
          return this.wallet.controller.transferV2(getSignableTransferV2Result);
        },
      ),
      TE.bind(
        'result',
        ({
          getSignableTransferV2Result,
          transferRequestWithSignatures,
          ethSignature,
        }) =>
          this.post(
            'transfers',
            {
              sender_stark_key: getSignableTransferV2Result.sender_stark_key,
              requests: transferRequestWithSignatures,
            },
            ImmutableMethodResults.ImmutableTransferV2ResultCodec.decode,
            V2,
            addEthHeaders(
              {},
              ethSignature.ethAddress,
              ethSignature.ethSignature,
            ),
          ),
      ),
      TE.map(({ result }) => result),
    );
  }

