export const initIframeDom = (
  fullIframeOptions: FullIframeOptions,
  url: string,
  onCloseIframe?: () => void,
) => {
  const styleSheet = document.createElement('style');
  const containerDom = document.createElement('div');
  const iframeDom = document.createElement('iframe');
  const closeButtonDom = document.createElement('button');
  containerDom.classList.add('imxLinkIframeContainer');
  fullIframeOptions.className &&
    containerDom.classList.add(fullIframeOptions.className);
  if (fullIframeOptions.hidden) containerDom.setAttribute('hidden', 'true');
  iframeDom.classList.add('imxLinkIframeContainer__iframe');
  iframeDom.setAttribute(
    'sandbox',
    'allow-same-origin allow-scripts allow-forms allow-popups',
  );
  closeButtonDom.classList.add('imxLinkIframeContainer__closeButton');
  styleSheet.append(
    document.createTextNode(renderEmbedStyles(fullIframeOptions)),
  );
  containerDom.append(styleSheet, iframeDom, closeButtonDom);

  // @NOTE: set the source of the iframe, and finally inject the iframe
  // container into the document:
  iframeDom.setAttribute('src', url);
  fullIframeOptions.containerElement.appendChild(containerDom);
  iframeDom.addEventListener('load', () => {
    console.log('iframe content loaded');
  });

  if (onCloseIframe) {
    closeButtonDom.addEventListener('click', onCloseIframe);
  }

  return { containerDom, iframeDom };
};

export class Link {
  constructor(
    private webUrl = 'https://link.sandbox.x.immutable.com',
    private iframeOptions: ConfigurableIframeOptions = null,
  ) {}

  private buildUrl(route: Routes, params: any, legacy: boolean) {
    if (messagingUrls.includes(route)) {
      return this.webUrl;
    }

    let query = params || {};

    if (
      route === Routes.Sell ||
      route === Routes.BuyV2 ||
      route === Routes.Cancel
    ) {
      // These routes could have a list of fee objects for their params.
      // queryString.stringifyUrl() doesn't work for lists of objects. We need
      // to convert the fees param to two separate comma separated strings.
      if (Array.isArray(params.fees)) {
        const fees = params.fees as FeeType[];
        query = {
          ...params,
          fees: undefined,
          fee_percentages: fees.map(fee => fee.percentage),
          fee_recipients: fees.map(fee => fee.recipient),
        };
      }
    }

    return legacy
      ? queryString.stringifyUrl({
          url: `${this.webUrl}/${route}`,
          query,
        })
      : `${this.webUrl}/${route}${qs.stringify(params, STRINGIFY_SETTINGS)}`;
  }

  private openIframeOrWindow =
    <I, O = void>(route: Routes) =>
    (params: I): Promise<O> =>
      this.iframeOptions
        ? this.openIframe(route, params)
        : this.openWindow(route, params);

  private openIframe = <I, O = void>(route: Routes, params: I): Promise<O> =>
    new Promise((resolve, reject) => {
      const url = this.buildUrl(route, params, route !== Routes.TransferV2);
      const unloadIframe = () => {
        window.removeEventListener('message', eventListener, false);
        fullIframeOptions.containerElement.removeChild(containerDom);
        reject(new LinkError(1003, 'Code 1003 - Link window closed.'));
      };

      const fullIframeOptions: FullIframeOptions = {
        ...getFullIframeOptions(),
        ...this.iframeOptions,
        ...params,
      };
      const { containerDom, iframeDom } = initIframeDom(
        fullIframeOptions,
        url,
        unloadIframe,
      );

      const eventListener = (event: MessageEvent) => {
        if (event.origin !== this.getLinkDomain()) return;

        const { data: body } = event;

        if (body.type === LINK_MESSAGE_TYPE) {
          switch (body.message) {
            case messageTypes.inProgress: {
              console.log(`${route} In Progress`);
              break;
            }
            case messageTypes.ready: {
              console.log(`Link ready for commands`);
              switch (route) {
                case Routes.BatchNftTransfer:
                case Routes.Sign:
                  iframeDom?.contentWindow?.postMessage(
                    {
                      type: LINK_MESSAGE_TYPE,
                      message: messageTypesForRoute[route],
                      payload: params,
                    },
                    '*',
                  );
                  break;

                default:
                  console.error(`Error: Unknown route`);
                  break;
              }
              break;
            }
            case messageTypes.success: {
              console.log(`${route} Succeeded with`, body.data);
              resolve((body.data ?? {}) as O);
              break;
            }
            case messageTypes.fail: {
              console.log(`${route} Failed with`, body?.data ?? 'no data');
              if (body?.data?.code) {
                reject(new LinkError(body.data.code, body.data?.error ?? ''));
              } else reject();
              break;
            }
            // This is only used by setup function
            // TODO see if we can consolidate result and success
            case messageTypes.result: {
              console.log(`${route} Succeeded with result`, body.data);
              resolve((body.data ?? {}) as O);
              unloadIframe();
              break;
            }
            case messageTypes.close: {
              unloadIframe();
              break;
            }
            default: {
              console.error('Unknown message', body);
              break;
            }
          }
        }
      };

      window.addEventListener('message', eventListener, false);
    });

  private openWindow = <I, O = void>(route: Routes, params: I): Promise<O> =>
    new Promise((resolve, reject) => {
      const defaultOptions = getFullIframeOptions();
      const url = this.buildUrl(route, params, route !== Routes.TransferV2);
      const win = window.open(
        url,
        'imx-link',
        `menubar=yes,location=no,resizable=no,scrollbars=no,status=yes,width=${defaultOptions.size.width},height=${defaultOptions.size.height}`,
      );
      if (!win) {
        throw new Error('Unable to open window');
      }

      const checkClosed = setInterval(() => {
        if (win.closed) {
          clearInterval(checkClosed);
          reject(new LinkError(1003, 'Code 1003 - Link window closed.'));
        }
      }, 500);

      const eventListener = (event: MessageEvent) => {
        if (event.origin !== this.getLinkDomain()) return;
        const { data } = event;

        if (data.type === LINK_MESSAGE_TYPE) {
          switch (data.message) {
            case messageTypes.inProgress: {
              console.log(`${route} In Progress`);
              break;
            }
            case messageTypes.ready: {
              console.log(`Link ready for commands`);
              switch (route) {
                case Routes.BatchNftTransfer:
                case Routes.Sign:
                  win.postMessage(
                    {
                      type: LINK_MESSAGE_TYPE,
                      message: messageTypesForRoute[route],
                      payload: params,
                    },
                    '*',
                  );
                  break;

                default:
                  console.error(`Error: Unknown route`);
                  break;
              }
              break;
            }
            case messageTypes.success: {
              console.log(`${route} Succeeded`, data.data);
              resolve((data.data ?? {}) as O);
              break;
            }

            case messageTypes.fail: {
              console.log(`${route} Failed`);
              reject();
              break;
            }

            case messageTypes.result: {
              console.log(`${route} Succeeded with result`, data.data);
              window.removeEventListener('message', eventListener, false);
              clearInterval(checkClosed);
              win.close();
              resolve((data.data ?? {}) as O);
              break;
            }

            case messageTypes.close: {
              window.removeEventListener('message', eventListener, false);
              clearInterval(checkClosed);
              win.close();
              reject(new LinkError(1003, 'Code 1003 - Link window closed.'));
              break;
            }

            default: {
              console.error('Unknown message', data);
              break;
            }
          }
        }
      };

      window.addEventListener('message', eventListener, false);
    });

  private getLinkDomain = () => new URL(this.webUrl).origin;

  history = this.openIframeOrWindow<LinkParams.History>(Routes.History);

  setup = async (params: LinkParams.Setup): Promise<LinkResults.Setup> => {
    const result = await this.openIframeOrWindow<
      LinkParams.Setup,
      LinkResults.Setup
    >(Routes.Setup)(params);

    window.localStorage.setItem(
      LocalStorageKeys.PROVIDER_PREFERENCE,
      result.providerPreference,
    );

    return result;
  };

  buy = this.openIframeOrWindow<LinkParams.BuyV2, LinkResults.BuyV2>(
    Routes.BuyV2,
  );

  sell = this.openIframeOrWindow<LinkParams.Sell>(Routes.Sell);

  deposit = (params: LinkParams.FlexibleDeposit = undefined) => {
    const openDeposit = () =>
      this.openIframeOrWindow<LinkParams.FlexibleDeposit>(Routes.Deposit)(
        params,
      );
    return openDeposit();
  };

  prepareWithdrawal = this.openIframeOrWindow<
    LinkParams.PrepareWithdrawal,
    LinkResults.PrepareWithdrawal
  >(Routes.PrepareWithdrawal);

  completeWithdrawal = this.openIframeOrWindow<
    LinkParams.CompleteWithdrawal,
    LinkResults.CompleteWithdrawal
  >(Routes.CompleteWithdrawal);

  // Sends individual transfer requests to v1 endpoint in a loop
  transfer = this.openIframeOrWindow<
    LinkParams.TransferV2,
    LinkResults.TransferV2
  >(Routes.TransferV2);

  // Sends NFT transfer requests to v2 endpoint in batches
  batchNftTransfer = async (params: LinkParams.BatchNftTransfer) => {
    const isNonNftTokensFound =
      params.filter(transfer => transfer.type !== ERC721TokenType.ERC721)
        .length > 0;
    if (isNonNftTokensFound) {
      throw Error('batchNftTransfer can only be called with NFT transfers');
    }

    return this.openIframeOrWindow<
      LinkParams.BatchNftTransfer,
      LinkResults.BatchNftTransfer
    >(Routes.BatchNftTransfer)(params);
  };

  cancel = this.openIframeOrWindow<LinkParams.Cancel>(Routes.Cancel);

  claim = async () => {
    console.warn(
      "Warning: 'Link.claim()' is only for the 'IMX' reward campaign.",
    );
    const openClaim = this.openIframeOrWindow<LinkParams.Claim>(Routes.Claim);
    return openClaim({});
  };

  onramp = this.openIframeOrWindow<LinkParams.Onramp, LinkResults.Onramp>(
    Routes.Onramp,
  );

  offramp = this.openIframeOrWindow<LinkParams.Offramp, LinkResults.Offramp>(
    Routes.Offramp,
  );

  /**
   * @deprecated
   * Exchange API v3 uses on 'Onramp' instead of 'FiatToCrypto'
   */
  fiatToCrypto = this.openIframeOrWindow<
    LinkParams.FiatToCrypto,
    LinkResults.FiatToCrypto
  >(Routes.FiatToCrypto);

  /**
   * @deprecated
   * Exchange API v3 uses on 'Offramp' instead of 'CryptoToFiat'
   */
  cryptoToFiat = this.openIframeOrWindow<
    LinkParams.CryptoToFiat,
    LinkResults.CryptoToFiat
  >(Routes.CryptoToFiat);

  nftCheckoutPrimary = this.openIframeOrWindow<
    LinkParams.NFTCheckoutPrimary,
    LinkResults.NFTCheckoutPrimary
  >(Routes.NFTCheckoutPrimary);

  nftCheckoutSecondary = this.openIframeOrWindow<
    LinkParams.NFTCheckoutSecondary,
    LinkResults.NFTCheckoutSecondary
  >(Routes.NFTCheckoutSecondary);

  makeOffer = (params: LinkParams.MakeOffer) =>
    experimental<LinkResults.MakeOffer>(
      () =>
        this.openIframeOrWindow<LinkParams.MakeOffer, LinkResults.MakeOffer>(
          Routes.MakeOffer,
        )(params),
      {
        runCondition: true,
        checkReferrer: true,
        message:
          'The method link.makeOffer() is experimental and should not be used in production',
      },
    );

  cancelOffer = (params: LinkParams.CancelOffer) =>
    experimental(
      () =>
        this.openIframeOrWindow<LinkParams.CancelOffer>(Routes.CancelOffer)(
          params,
        ),
      {
        runCondition: true,
        checkReferrer: true,
        message:
          'The method link.cancelOffer() is experimental and should not be used in production',
      },
    );

  acceptOffer = (params: LinkParams.AcceptOffer) =>
    experimental(
      () =>
        this.openIframeOrWindow<LinkParams.AcceptOffer>(Routes.AcceptOffer)(
          params,
        ),
      {
        runCondition: true,
        checkReferrer: true,
        message:
          'The method link.acceptOffer() is experimental and should not be used in production',
      },
    );

  sign = this.openIframeOrWindow<LinkParams.Sign, LinkResults.Sign>(
    Routes.Sign,
  );

  getPublicKey = this.openIframeOrWindow<
    LinkParams.GetPublicKey,
    LinkResults.GetPublicKey
  >(Routes.GetPublicKey);
}
export const PERCENTAGE_DECIMAL_PLACES_MULTIPLIER = 10000;
export const MINIMUM_BASE_PRICE_ERROR_MESSAGE =
  'Cannot calculate fee breakdown (with percentages to 2 decimal places) for base price lower than BigNumber of 10000';
export const FEE_DECIMAL_PLACES_ERROR_MESSAGE =
  'Fees can only be specified to 2 decimal places';
export const PRICE_WITH_FEES_TOO_SMALL_ERROR_MESSAGE =
  'priceWithFees is too small to support supplied fee percentages';
export const MINIMUM_BASE_PRICE_VALUE = 10000;
import { BigNumber } from 'ethers';

import { PERCENTAGE_DECIMAL_PLACES_MULTIPLIER } from './constants';
import { FeeBreakdownResult } from './feeBreakdown.models';

export type FeeAmount = {
  amount: BigNumber;
};

export interface FeeBreakdownFromPriceAmountsInputs {
  priceWithFees: BigNumber;
  protocolFeeAmounts: FeeAmount[];
  royaltyFeeAmounts: FeeAmount[];
  ecosystemFeeAmounts: FeeAmount[];
}

function calculatePercentageFromAmounts(fractionAmount, totalAmount) {
  return (
    // percentage = fraction * 100 / total
    // because we support fee percentages accurate to 2 decimal places e.g 0.01% or 0.0001 as a fraction
    // and BigNumber from ethers library will error if it ever evaluates a floating point number
    // we multiply up by 10000, then convert to a javascript number then divide back by 10000
    // to get an accurate javascript floating point number
    parseInt(
      fractionAmount
        .mul(100)
        .mul(PERCENTAGE_DECIMAL_PLACES_MULTIPLIER)
        .div(totalAmount)
        .toBigInt()
        .toString(),
      10,
    ) / PERCENTAGE_DECIMAL_PLACES_MULTIPLIER
  );
}

function addFeeAmounts(feeAmounts) {
  return feeAmounts.reduce(
    (acc, ecosystemFee) => acc.add(ecosystemFee.amount),
    BigNumber.from(0),
  );
}
export function calculateFeeBreakdownFromPriceAmounts({
  priceWithFees,
  protocolFeeAmounts,
  royaltyFeeAmounts,
  ecosystemFeeAmounts,
}: FeeBreakdownFromPriceAmountsInputs): FeeBreakdownResult {
  const totalProtocolFeeAmount = addFeeAmounts(protocolFeeAmounts);
  const totalRoyaltyFeeAmount = addFeeAmounts(royaltyFeeAmounts);
  const totalEcosystemFeeAmount = addFeeAmounts(ecosystemFeeAmounts);

  const basePrice = priceWithFees
    .sub(totalProtocolFeeAmount)
    .sub(totalRoyaltyFeeAmount)
    .sub(totalEcosystemFeeAmount);

  const protocolPercentage = calculatePercentageFromAmounts(
    totalProtocolFeeAmount,
    basePrice,
  );

  const royaltyPercentage = calculatePercentageFromAmounts(
    totalRoyaltyFeeAmount,
    basePrice,
  );

  const ecosystemPercentage = calculatePercentageFromAmounts(
    totalEcosystemFeeAmount,
    basePrice,
  );

  // for js floating point arithemtic accuracy
  const totalFeePercentage =
    (protocolPercentage * 100 +
      royaltyPercentage * 100 +
      ecosystemPercentage * 100) /
    100;

  return {
    basePrice,
    priceWithFees,
    feeBreakdown: {
      protocol: {
        amount: totalProtocolFeeAmount,
        percentage: protocolPercentage,
      },
      royalty: {
        amount: totalRoyaltyFeeAmount,
        percentage: royaltyPercentage,
      },
      ecosystem: {
        amount: totalEcosystemFeeAmount,
        percentage: ecosystemPercentage,
      },
      totalFeePercentage,
    },
  } as FeeBreakdownResult;
}
import { FeeType } from '../../types';
import {
  FEE_DECIMAL_PLACES_ERROR_MESSAGE,
  PERCENTAGE_DECIMAL_PLACES_MULTIPLIER,
} from './constants';

export function addFeePercentages(fees: FeeType[]) {
  return fees.reduce((acc, fee) => {
    return (
      parseInt(
        (
          acc * PERCENTAGE_DECIMAL_PLACES_MULTIPLIER +
          fee.percentage * PERCENTAGE_DECIMAL_PLACES_MULTIPLIER
        ).toFixed(0),
        10,
      ) / PERCENTAGE_DECIMAL_PLACES_MULTIPLIER
    );
  }, 0);
}

export function validateFeePercentagesToTwoDecimalPlaces(fees: FeeType[]) {
  fees.forEach(fee => {
    if (
      fee.percentage.toString().includes('.') &&
      fee.percentage.toString().split('.')[1].length > 2
    ) {
      throw new Error(FEE_DECIMAL_PLACES_ERROR_MESSAGE);
    }
  });
}
import { BigNumber } from 'ethers';

import { FeeType, PositiveBigNumber } from '../../types';
import {
  MINIMUM_BASE_PRICE_ERROR_MESSAGE,
  MINIMUM_BASE_PRICE_VALUE,
  PERCENTAGE_DECIMAL_PLACES_MULTIPLIER,
} from './constants';
import {
  FeeBreakdownFromBasePriceInputs,
  FeeBreakdownResult,
} from './feeBreakdown.models';
import {
  addFeePercentages,
  validateFeePercentagesToTwoDecimalPlaces,
} from './shared';

function calculateFeesOnBasePrice(
  basePrice: BigNumber,
  fees: FeeType[],
): { totalFeePercentage: number; totalFeeAmount: BigNumber } {
  const totalFeePercentage = addFeePercentages(fees);

  const totalFeePercentageConverted = parseInt(
    (totalFeePercentage * PERCENTAGE_DECIMAL_PLACES_MULTIPLIER).toFixed(0),
    10,
  );

  const totalFeeAmount = basePrice
    .mul(BigNumber.from(totalFeePercentageConverted))
    .div(BigNumber.from(PERCENTAGE_DECIMAL_PLACES_MULTIPLIER))
    .div(BigNumber.from(100));

  return {
    totalFeePercentage,
    totalFeeAmount,
  };
}

// Return the fee breakdown by specifying the base price and the fee percentages to be applied
export function calculateFeeBreakdownFromBasePrice({
  basePrice,
  protocolFees,
  royaltyFees,
  makerFees,
  takerFees,
}: FeeBreakdownFromBasePriceInputs): FeeBreakdownResult {
  if (basePrice.lt(BigNumber.from(MINIMUM_BASE_PRICE_VALUE))) {
    // lowest valid percentage fee of 0.01 % multiplied by base price would result in an invalid amount
    // e.g 1,000 wei * 0.01% is less than 1 wei which is an invalid value
    // 10,000 wei * 0.01% is 1 wei which is the lowest possible fee amount value
    throw new Error(MINIMUM_BASE_PRICE_ERROR_MESSAGE);
  }

  validateFeePercentagesToTwoDecimalPlaces([
    ...(protocolFees ?? []),
    ...royaltyFees,
    ...makerFees,
    ...takerFees,
  ]);

  let priceWithFees = basePrice;

  const {
    totalFeePercentage: totalProtocolFeePercentage,
    totalFeeAmount: totalProtocolFeeAmount,
  } = calculateFeesOnBasePrice(basePrice, protocolFees ?? []);

  priceWithFees = priceWithFees.add(totalProtocolFeeAmount);

  const {
    totalFeePercentage: totalRoyaltyFeePercentage,
    totalFeeAmount: totalRoyaltyFeeAmount,
  } = calculateFeesOnBasePrice(basePrice, royaltyFees);

  priceWithFees = priceWithFees.add(totalRoyaltyFeeAmount);

  const {
    totalFeePercentage: totalMakerFeePercentage,
    totalFeeAmount: totalMakerFeeAmount,
  } = calculateFeesOnBasePrice(basePrice, makerFees);

  priceWithFees = priceWithFees.add(totalMakerFeeAmount);

  const {
    totalFeePercentage: totalTakerFeePercentage,
    totalFeeAmount: totalTakerFeeAmount,
  } = calculateFeesOnBasePrice(basePrice, takerFees);

  priceWithFees = priceWithFees.add(totalTakerFeeAmount);

  const totalEcosystemFeePercentage =
    totalMakerFeePercentage + totalTakerFeePercentage;

  const totalEcosystemFeeAmount = totalMakerFeeAmount.add(totalTakerFeeAmount);

  const totalFeePercentage =
    (totalProtocolFeePercentage * 100 +
      totalRoyaltyFeePercentage * 100 +
      totalMakerFeePercentage * 100 +
      totalTakerFeePercentage * 100) /
    100;

  return {
    basePrice: basePrice as PositiveBigNumber,
    priceWithFees: priceWithFees as PositiveBigNumber,
    feeBreakdown: {
      protocol: {
        percentage: totalProtocolFeePercentage,
        amount: totalProtocolFeeAmount as PositiveBigNumber,
      },
      royalty: {
        percentage: totalRoyaltyFeePercentage,
        amount: totalRoyaltyFeeAmount as PositiveBigNumber,
      },
      ecosystem: {
        percentage: totalEcosystemFeePercentage,
        amount: totalEcosystemFeeAmount as PositiveBigNumber,
      },
      totalFeePercentage,
    },
  };
}
import { BigNumber } from 'ethers';

import { PositiveBigNumber } from '../../types';
import {
  MINIMUM_BASE_PRICE_VALUE,
  PRICE_WITH_FEES_TOO_SMALL_ERROR_MESSAGE,
} from './constants';
import {
  FeeBreakdownFromFullPriceInputs,
  FeeBreakdownResult,
} from './feeBreakdown.models';
import {
  addFeePercentages,
  validateFeePercentagesToTwoDecimalPlaces,
} from './shared';

function calculateAmountFromFractionPercentage(
  priceWithFees: BigNumber,
  totalPercentage: number,
  fractionPercentage: number,
) {
  return priceWithFees
    .div(BigNumber.from(totalPercentage * 100))
    .mul(BigNumber.from(fractionPercentage * 100));
}

export function calculateFeeBreakdownFromFullPrice({
  priceWithFees,
  protocolFees,
  royaltyFees,
  makerFees,
  takerFees,
}: FeeBreakdownFromFullPriceInputs): FeeBreakdownResult {
  validateFeePercentagesToTwoDecimalPlaces([
    ...(protocolFees ?? []),
    ...royaltyFees,
    ...makerFees,
    ...takerFees,
  ]);

  const totalProtocolPercentages = addFeePercentages(protocolFees ?? []);
  const totalRoyaltyPercentages = addFeePercentages(royaltyFees);
  const totalEcosystemPercentages = addFeePercentages([
    ...makerFees,
    ...takerFees,
  ]);
  const totalFeePercentages = addFeePercentages([
    ...(protocolFees ?? []),
    ...royaltyFees,
    ...makerFees,
    ...takerFees,
  ]);
  const totalFraction = 100 + totalFeePercentages;

  const basePrice = priceWithFees
    .div(BigNumber.from(totalFraction * 100))
    .mul(BigNumber.from(100))
    .mul(BigNumber.from(100));

  if (basePrice.lt(BigNumber.from(MINIMUM_BASE_PRICE_VALUE))) {
    throw new Error(PRICE_WITH_FEES_TOO_SMALL_ERROR_MESSAGE);
  }

  const protocolAmount = calculateAmountFromFractionPercentage(
    priceWithFees,
    totalFraction,
    totalProtocolPercentages,
  );

  const royaltyAmount = calculateAmountFromFractionPercentage(
    priceWithFees,
    totalFraction,
    totalRoyaltyPercentages,
  );

  const ecosystemAmount = calculateAmountFromFractionPercentage(
    priceWithFees,
    totalFraction,
    totalEcosystemPercentages,
  );

  return {
    basePrice: basePrice as PositiveBigNumber,
    priceWithFees: priceWithFees as PositiveBigNumber,
    feeBreakdown: {
      protocol: {
        percentage: totalProtocolPercentages,
        amount: protocolAmount as PositiveBigNumber,
      },
      royalty: {
        percentage: totalRoyaltyPercentages,
        amount: royaltyAmount as PositiveBigNumber,
      },
      ecosystem: {
        percentage: totalEcosystemPercentages,
        amount: ecosystemAmount as PositiveBigNumber,
      },
      totalFeePercentage: totalFeePercentages,
    },
  };
}
